import React, { useState } from "react";
import {
  BarChart,
  TrendingUp,
  Users,
  Heart,
  Activity,
  DollarSign,
  MessageSquare,
  Star,
  Bell,
  AlertCircle,
  Calendar,
  Clock,
  FileText,
  ChevronRight,
  ArrowRight
} from "lucide-react";

const MetricCard = ({ icon: Icon, title, value, trend, chart }) => (
  <div className="bg-white rounded-xl shadow-sm border border-slate-200 p-6">
    <div className="flex justify-between items-start mb-4">
