import React, { useState } from "react";

const Clients = () => {
  const [clients, setClients] = useState([
    // Example data
    {
      id: 1,
      name: "John Doe",
      email: "john.doe@example.com",
      phone: "123-456-7890",
      nurse: "Nurse Mary",
      status: "Active",
      lastActivity: "Welfare Call - Jan 10, 2025",
    },
    {
      id: 2,
      name: "Jane Smith",
      email: "jane.smith@example.com",
      phone: "987-654-3210",
      nurse: "Nurse Peter",
      status: "Inactive",
      lastActivity: "Consultation - Dec 15, 2024",
    },
  ]);

  const handleSearch = (e) => {
    // Search functionality
    const searchTerm = e.target.value.toLowerCase();
    const filteredClients = clients.filter((client) =>
      client.name.toLowerCase().includes(searchTerm)
    );
    setClients(filteredClients);
  };

  return (
    <div className="max-w-7xl mx-auto mt-28 px-6">
      {/* Matches header width */}
      <h1 className="text-3xl font-bold mb-4 text-center">Clients</h1>

      {/* Search and Add New Client */}
      <div className="flex justify-between items-center mb-4">
        <input
          type="text"
          placeholder="Search clients..."
          className="border border-gray-300 rounded-lg px-4 py-2 w-2/3"
          onChange={handleSearch}
        />
        <button className="bg-blue-600 text-white px-4 py-2 rounded-lg">
          Add New Client
        </button>
      </div>

      {/* Clients Table */}
      <table className="w-full border border-gray-300">
        <thead className="bg-gray-100">
          <tr>
            <th className="border px-4 py-2 text-left">Name</th>
            <th className="border px-4 py-2 text-left">Contact Info</th>
            <th className="border px-4 py-2 text-left">Assigned Nurse</th>
            <th className="border px-4 py-2 text-left">Status</th>
            <th className="border px-4 py-2 text-left">Last Activity</th>
            <th className="border px-4 py-2 text-left">Actions</th>
          </tr>
        </thead>
        <tbody>
          {clients.map((client) => (
            <tr key={client.id} className="hover:bg-gray-100">
              <td className="border px-4 py-2">{client.name}</td>
              <td className="border px-4 py-2">{client.email}</td>
              <td className="border px-4 py-2">{client.nurse}</td>
              <td className="border px-4 py-2">{client.status}</td>
              <td className="border px-4 py-2">{client.lastActivity}</td>
              <td className="border px-4 py-2">
                <button className="text-blue-600 hover:underline">View</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Clients;
