import React, { useState } from "react";
import SelfNurseHeader from "../../components/selfemployed/Self-NurseHeader"; // Adjust path if necessary
import {
  Search,
  User,
  FileText,
  ChevronRight,
  MessageSquare,
  Calendar,
  Heart,
} from "lucide-react";

const ClientCard = ({ name, age, contact, healthStatus }) => (
  <div className="flex items-center justify-between p-4 bg-white rounded-xl border border-slate-200 hover:shadow-sm transition-all">
    <div className="flex items-center gap-4">
      <div className="w-12 h-12 rounded-full bg-gradient-to-br from-emerald-50 to-[#A390E4]/10 flex items-center justify-center">
        <User className="w-6 h-6 text-[#A390E4]" />
      </div>
      <div>
        <h3 className="font-medium text-slate-900">{name}</h3>
        <p className="text-sm text-slate-600">Age: {age}</p>
        <p className="text-sm text-slate-600">Contact: {contact}</p>
      </div>
    </div>
    <div className="flex items-center space-x-4">
      <div className={`px-3 py-1 rounded-full text-xs font-medium ${healthStatus === "Good" ? "bg-emerald-50 text-emerald-600" : "bg-red-50 text-red-600"}`}>
        {healthStatus}
      </div>
      <ChevronRight className="w-5 h-5 text-slate-400" />
    </div>
  </div>
);

const Clients = () => {
  const [searchQuery, setSearchQuery] = useState("");

  const clients = [
    { name: "John Doe", age: 45, contact: "555-1234", healthStatus: "Good" },
    { name: "Jane Smith", age: 60, contact: "555-5678", healthStatus: "Critical" },
    { name: "Emily Brown", age: 30, contact: "555-9876", healthStatus: "Good" },
  ];

  const filteredClients = clients.filter((client) =>
    client.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gradient-to-b from-emerald-50 via-white to-emerald-50">
      <SelfNurseHeader />
      <main className="max-w-7xl mx-auto px-4 py-8 pt-24">
        {/* Header Section */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-emerald-700">
            Clients<span className="text-[#A390E4]"> Overview</span>
          </h1>
          <p className="text-lg text-slate-600">
            Access and manage details of your clients.
          </p>
        </div>

        {/* Search Section */}
        <div className="flex items-center gap-4 mb-6">
          <div className="relative w-full">
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Search clients..."
              className="w-full px-4 py-2 border border-slate-300 rounded-lg shadow-sm focus:outline-none focus:ring focus:ring-[#A390E4]"
            />
            <Search className="absolute right-3 top-2.5 w-5 h-5 text-slate-400" />
          </div>
        </div>

        {/* Client List */}
        <div className="grid gap-4">
          {filteredClients.length > 0 ? (
            filteredClients.map((client, index) => (
              <ClientCard key={index} {...client} />
            ))
          ) : (
            <p className="text-slate-600">No clients found.</p>
          )}
        </div>
      </main>
    </div>
  );
};

export default Clients;
