import React, { useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import "react-big-calendar/lib/css/react-big-calendar.css";
import moment from "moment";
import DashboardHeader from "../../../components/layout/careseeker/DashboardHeader";

const localizer = momentLocalizer(moment);

const CalendarPage = () => {
  const [events, setEvents] = useState([
    {
      title: "Doctor's Appointment",
      start: new Date(2025, 0, 15, 10, 0),
      end: new Date(2025, 0, 15, 11, 0),
      type: "Medical",
      description: "Visit Dr. Smith for a routine check-up.",
    },
    {
      title: "Family Meeting",
      start: new Date(2025, 0, 20, 14, 0),
      end: new Date(2025, 0, 20, 15, 30),
      type: "Social",
      description: "Discuss the upcoming family vacation.",
    },
  ]);

  const handleSelectSlot = ({ start, end }) => {
    const title = window.prompt("Enter the event title:");
    const type = window.prompt("Enter the event type (e.g., Medical, Social, Reminder):");
    const description = window.prompt("Enter a description for the event:");
    if (title && type) {
      setEvents([...events, { title, start, end, type, description }]);
    }
  };

  const handleSelectEvent = (event) => {
    if (window.confirm(`Do you want to delete the event: '${event.title}'?`)) {
      setEvents(events.filter((e) => e !== event));
    }
  };

  return (
    <div>
      <DashboardHeader />
      <div className="max-w-7xl mx-auto px-4 pt-20 pb-8">
        <h1 className="text-3xl font-bold text-gray-800 mb-6">Your Calendar</h1>
        <p className="text-gray-600 mb-4">
          Manage appointments, reminders, and schedule with ease.
        </p>

        {/* Options Section */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
          {/* Quick Actions */}
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Quick Actions</h2>
            <ul className="list-disc list-inside text-gray-600">
              <li>Add medical appointments with detailed descriptions.</li>
              <li>Set reminders for medication and important activities.</li>
              <li>Schedule social events or visits from family and friends.</li>
              <li>View daily, weekly, and monthly schedules in one place.</li>
            </ul>
          </div>

          {/* Notifications */}
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Notifications</h2>
            <p className="text-gray-600 mb-4">
              Enable notifications to stay updated on upcoming events and reminders.
            </p>
            <button className="bg-emerald-500 text-white px-4 py-2 rounded-lg hover:bg-emerald-600">
              Enable Notifications
            </button>
          </div>

          {/* Event Categories */}
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Event Categories</h2>
            <p className="text-gray-600 mb-4">
              Filter and color-code events based on categories:
            </p>
            <ul className="list-disc list-inside text-gray-600">
              <li>
                <span className="text-blue-600 font-bold">Medical:</span> Doctor visits,
                health check-ups.
              </li>
              <li>
                <span className="text-green-600 font-bold">Social:</span> Family meetings,
                friend visits.
              </li>
              <li>
                <span className="text-yellow-600 font-bold">Reminder:</span> Medication,
                exercise routines.
              </li>
            </ul>
          </div>

          {/* Sharing Calendar */}
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-xl font-semibold text-gray-800 mb-4">Share Your Calendar</h2>
            <p className="text-gray-600 mb-4">
              Allow family and friends to view or update your calendar. Grant access to
              specific individuals for better coordination.
            </p>
            <button className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600">
              Share Calendar
            </button>
          </div>
        </div>

        {/* Calendar Component */}
        <div className="bg-white p-6 rounded-lg shadow-md">
          <Calendar
            localizer={localizer}
            events={events.map((event) => ({
              ...event,
              title: `${event.type}: ${event.title}`,
            }))}
            startAccessor="start"
            endAccessor="end"
            selectable
            popup
            style={{ height: 500 }}
            onSelectSlot={handleSelectSlot}
            onSelectEvent={(event) => alert(`${event.title}\n\n${event.description}`)}
          />
        </div>
      </div>
    </div>
  );
};

export default CalendarPage;
