import React, { useState } from "react";

const Messages = () => {
  const [activeCategory, setActiveCategory] = useState("Careseekers");
  const [selectedContact, setSelectedContact] = useState(null);

  // Mock data for contacts
  const contacts = {
    Careseekers: ["John Doe", "Jane Smith"],
    Families: ["Emily Doe", "Michael Smith"],
    Staff: ["Nurse Clara", "Dr. Adams"],
    Clients: ["Client A", "Client B"],
  };

  // Placeholder for messages
  const messages = {
    "John Doe": ["Hi there! How are you?", "Looking forward to our next session."],
    "Emily Doe": ["Can we schedule a follow-up call?"],
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <aside className="w-1/4 bg-emerald-100 border-r">
        <div className="p-4">
          <h2 className="text-lg font-bold text-emerald-700">Message Categories</h2>
          <ul className="space-y-2 mt-4">
            {Object.keys(contacts).map((category) => (
              <li
                key={category}
                className={`p-2 rounded cursor-pointer ${
                  activeCategory === category ? "bg-emerald-300 text-white" : "hover:bg-emerald-200"
                }`}
                onClick={() => {
                  setActiveCategory(category);
                  setSelectedContact(null);
                }}
              >
                {category}
              </li>
            ))}
          </ul>
        </div>
        <div className="p-4">
          <h3 className="text-md font-bold text-emerald-700">{activeCategory}</h3>
          <ul className="mt-2 space-y-2">
            {contacts[activeCategory].map((contact) => (
              <li
                key={contact}
                className={`p-2 rounded cursor-pointer ${
                  selectedContact === contact ? "bg-emerald-300 text-white" : "hover:bg-emerald-200"
                }`}
                onClick={() => setSelectedContact(contact)}
              >
                {contact}
              </li>
            ))}
          </ul>
        </div>
      </aside>

      {/* Main Messaging Area */}
      <main className="flex-1 p-4">
        {selectedContact ? (
          <>
            <div className="border-b pb-4 mb-4">
              <h2 className="text-xl font-bold text-emerald-700">Chat with {selectedContact}</h2>
            </div>
            <div className="flex flex-col h-full">
              {/* Message History */}
              <div className="flex-1 overflow-y-auto">
                {messages[selectedContact]?.map((message, index) => (
                  <div
                    key={index}
                    className={`p-2 my-2 rounded ${
                      index % 2 === 0 ? "bg-emerald-100 self-start" : "bg-emerald-300 self-end"
                    }`}
                  >
                    {message}
                  </div>
                ))}
              </div>

              {/* Message Input */}
              <div className="border-t mt-4 pt-4">
                <textarea
                  className="w-full p-2 border rounded focus:outline-emerald-400"
                  placeholder="Type your message..."
                  rows={3}
                />
                <div className="flex justify-end mt-2 space-x-2">
                  <button className="bg-emerald-600 text-white px-4 py-2 rounded hover:bg-emerald-700">
                    Send
                  </button>
                  <button className="bg-gray-200 text-gray-700 px-4 py-2 rounded hover:bg-gray-300">
                    Attach
                  </button>
                </div>
              </div>
            </div>
          </>
        ) : (
          <div className="flex items-center justify-center h-full">
            <p className="text-gray-500">Select a contact to start messaging.</p>
          </div>
        )}
      </main>
    </div>
  );
};

export default Messages;
