import React, { useState } from "react";

const Clients = () => {
  const [clients, setClients] = useState([
    {
      id: 1,
      name: "John Doe",
      email: "john.doe@example.com",
      phone: "123-456-7890",
      nurse: "Nurse Mary",
      status: "Active",
      lastActivity: "Welfare Call - Jan 10, 2025",
    },
    {
      id: 2,
      name: "Jane Smith",
      email: "jane.smith@example.com",
      phone: "987-654-3210",
      nurse: "Nurse Peter",
      status: "Inactive",
      lastActivity: "Consultation - Dec 15, 2024",
    },
    // Add more client data as needed
  ]);

  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5);
  const [sortConfig, setSortConfig] = useState(null);

  // Filtered clients for search
  const filteredClients = clients.filter((client) =>
    client.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Sorting functionality
  const sortedClients = [...filteredClients].sort((a, b) => {
    if (!sortConfig) return 0;
    const { key, direction } = sortConfig;
    if (a[key] < b[key]) return direction === "ascending" ? -1 : 1;
    if (a[key] > b[key]) return direction === "ascending" ? 1 : -1;
    return 0;
  });

  // Pagination
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentClients = sortedClients.slice(indexOfFirstItem, indexOfLastItem);

  const handleSearch = (e) => setSearchTerm(e.target.value);

  const handleSort = (key) => {
    let direction = "ascending";
    if (sortConfig && sortConfig.key === key && sortConfig.direction === "ascending") {
      direction = "descending";
    }
    setSortConfig({ key, direction });
  };

  const handlePageChange = (pageNumber) => setCurrentPage(pageNumber);

  const handleBulkAssign = () => {
    alert("Bulk assign functionality coming soon!");
  };

  const handleExport = () => {
    alert("Exporting data...");
  };

  const handleNotification = () => {
    alert("Send notification functionality coming soon!");
  };

  return (
    <div className="max-w-7xl mx-auto mt-28 px-6">
      {/* Page Title */}
      <h1 className="text-3xl font-bold mb-6 text-center">Clients</h1>

      {/* Search Bar */}
      <div className="flex justify-between items-center mb-6">
        <input
          type="text"
          placeholder="Search clients..."
          value={searchTerm}
          onChange={handleSearch}
          className="border border-gray-300 rounded-lg px-4 py-2 w-2/3"
        />
        <button
          onClick={() => alert("Add New Client functionality coming soon!")}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg"
        >
          Add New Client
        </button>
      </div>

      {/* Clients Table */}
      <table className="w-full border border-gray-300">
        <thead className="bg-gray-100">
          <tr>
            <th
              className="border px-4 py-2 text-left cursor-pointer"
              onClick={() => handleSort("name")}
            >
              Name
            </th>
            <th className="border px-4 py-2 text-left">Contact Info</th>
            <th
              className="border px-4 py-2 text-left cursor-pointer"
              onClick={() => handleSort("nurse")}
            >
              Assigned Nurse
            </th>
            <th
              className="border px-4 py-2 text-left cursor-pointer"
              onClick={() => handleSort("status")}
            >
              Status
            </th>
            <th
              className="border px-4 py-2 text-left cursor-pointer"
              onClick={() => handleSort("lastActivity")}
            >
              Last Activity
            </th>
            <th className="border px-4 py-2 text-left">Actions</th>
          </tr>
        </thead>
        <tbody>
          {currentClients.map((client) => (
            <tr key={client.id} className="hover:bg-gray-100">
              <td className="border px-4 py-2">{client.name}</td>
              <td className="border px-4 py-2">
                {client.email}
                <br />
                {client.phone}
              </td>
              <td className="border px-4 py-2">{client.nurse}</td>
              <td className="border px-4 py-2">
                <span
                  className={`px-2 py-1 rounded ${
                    client.status === "Active"
                      ? "bg-green-100 text-green-600"
                      : "bg-gray-100 text-gray-600"
                  }`}
                >
                  {client.status}
                </span>
              </td>
              <td className="border px-4 py-2">{client.lastActivity}</td>
              <td className="border px-4 py-2">
                <button
                  onClick={() => alert(`Viewing profile for ${client.name}`)}
                  className="text-blue-600 hover:underline"
                >
                  View
                </button>
                <button
                  onClick={() => alert(`Editing profile for ${client.name}`)}
                  className="text-green-600 hover:underline ml-4"
                >
                  Edit
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Bulk Actions */}
      <div className="flex justify-between items-center mt-6">
        <button
          onClick={handleBulkAssign}
          className="bg-gray-600 text-white px-4 py-2 rounded-lg"
        >
          Assign Clients to Nurse
        </button>
        <button
          onClick={handleExport}
          className="bg-gray-600 text-white px-4 py-2 rounded-lg"
        >
          Export to CSV
        </button>
        <button
          onClick={handleNotification}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg"
        >
          Send Notification
        </button>
      </div>

      {/* Pagination */}
      <div className="flex justify-center items-center mt-6 space-x-2">
        {[...Array(Math.ceil(filteredClients.length / itemsPerPage)).keys()].map(
          (number) => (
            <button
              key={number}
              onClick={() => handlePageChange(number + 1)}
              className={`px-4 py-2 rounded-lg ${
                currentPage === number + 1
                  ? "bg-blue-600 text-white"
                  : "bg-gray-200 text-gray-600"
              }`}
            >
              {number + 1}
            </button>
          )
        )}
      </div>
    </div>
  );
};

export default Clients;
