import React, { useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import "react-big-calendar/lib/css/react-big-calendar.css";
import moment from "moment";
import DashboardHeader from "../../components/layout/careseeker/DashboardHeader";

const localizer = momentLocalizer(moment);

const CalendarPage = () => {
  const [events, setEvents] = useState([
    {
      title: "Doctor's Appointment",
      start: new Date(2025, 0, 15, 10, 0),
      end: new Date(2025, 0, 15, 11, 0),
      category: "Medical",
    },
    {
      title: "Family Meeting",
      start: new Date(2025, 0, 20, 14, 0),
      end: new Date(2025, 0, 20, 15, 30),
      category: "Family",
    },
  ]);

  const handleSelectSlot = ({ start, end }) => {
    const title = window.prompt("Enter the event title:");
    const category = window.prompt("Enter the event category (e.g., Medical, Reminder, Family):");
    if (title) {
      setEvents([...events, { title, start, end, category }]);
    }
  };

  const handleSelectEvent = (event) => {
    if (window.confirm(`Do you want to delete the event: '${event.title}'?`)) {
      setEvents(events.filter((e) => e !== event));
    }
  };

  return (
    <div>
      <DashboardHeader />
      <div className="max-w-7xl mx-auto px-4 pt-16 pb-8">
        <h1 className="text-2xl font-bold text-gray-800 mb-6">Your Calendar</h1>
        <p className="text-gray-600 mb-4">
          Manage your appointments, reminders, and schedule with ease.
        </p>

        {/* Feature Details */}
        <div className="bg-gray-50 p-4 rounded-lg shadow-md mb-6">
          <h2 className="text-lg font-semibold text-gray-700">Features:</h2>
          <ul className="list-disc ml-6 text-gray-600">
            <li>Schedule medical, family, and personal events with categories.</li>
            <li>Set notifications and reminders for upcoming events.</li>
            <li>Shared access for family and friends to view and manage events.</li>
            <li>Details like event location, notes, and attachments.</li>
            <li>Seamless integration with external calendars like Google Calendar.</li>
          </ul>
        </div>

        <div className="bg-white p-6 rounded-lg shadow-md">
          <Calendar
            localizer={localizer}
            events={events}
            startAccessor="start"
            endAccessor="end"
            selectable
            style={{ height: 500 }}
            onSelectSlot={handleSelectSlot}
            onSelectEvent={handleSelectEvent}
          />
        </div>
      </div>
    </div>
  );
};

export default CalendarPage;
