import React, { useState } from "react";

const Nurses = () => {
  const [nurses, setNurses] = useState([
    {
      id: 1,
      name: "Mary Johnson",
      email: "mary.johnson@example.com",
      phone: "123-456-7890",
      clientsAssigned: 5,
      status: "Active",
      lastActivity: "Checked in with client - Jan 10, 2025",
    },
    {
      id: 2,
      name: "Peter Smith",
      email: "peter.smith@example.com",
      phone: "987-654-3210",
      clientsAssigned: 3,
      status: "Inactive",
      lastActivity: "Consultation completed - Dec 15, 2024",
    },
    // Add more nurse data as needed
  ]);

  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5);
  const [sortConfig, setSortConfig] = useState(null);

  // Filtered nurses for search
  const filteredNurses = nurses.filter((nurse) =>
    nurse.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Sorting functionality
  const sortedNurses = [...filteredNurses].sort((a, b) => {
    if (!sortConfig) return 0;
    const { key, direction } = sortConfig;
    if (a[key] < b[key]) return direction === "ascending" ? -1 : 1;
    if (a[key] > b[key]) return direction === "ascending" ? 1 : -1;
    return 0;
  });

  // Pagination
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentNurses = sortedNurses.slice(indexOfFirstItem, indexOfLastItem);

  const handleSearch = (e) => setSearchTerm(e.target.value);

  const handleSort = (key) => {
    let direction = "ascending";
    if (sortConfig && sortConfig.key === key && sortConfig.direction === "ascending") {
      direction = "descending";
    }
    setSortConfig({ key, direction });
  };

  const handlePageChange = (pageNumber) => setCurrentPage(pageNumber);

  const handleAssignClient = () => {
    alert("Assign client functionality coming soon!");
  };

  const handleExport = () => {
    alert("Exporting data...");
  };

  return (
    <div className="max-w-7xl mx-auto mt-20 px-6">
      {/* Page Title */}
      <h1 className="text-3xl font-bold mb-6 text-center">Nurses</h1>

      {/* Search Bar */}
      <div className="flex justify-between items-center mb-6">
        <input
          type="text"
          placeholder="Search nurses..."
          value={searchTerm}
          onChange={handleSearch}
          className="border border-gray-300 rounded-lg px-4 py-2 w-2/3"
        />
        <button
          onClick={() => alert("Add New Nurse functionality coming soon!")}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg"
        >
          Add New Nurse
        </button>
      </div>

      {/* Nurses Table */}
      <table className="w-full border border-gray-300">
        <thead className="bg-gray-100">
          <tr>
            <th
              className="border px-4 py-2 text-left cursor-pointer"
              onClick={() => handleSort("name")}
            >
              Name
            </th>
            <th className="border px-4 py-2 text-left">Contact Info</th>
            <th
              className="border px-4 py-2 text-left cursor-pointer"
              onClick={() => handleSort("clientsAssigned")}
            >
              Clients Assigned
            </th>
            <th
              className="border px-4 py-2 text-left cursor-pointer"
              onClick={() => handleSort("status")}
            >
              Status
            </th>
            <th
              className="border px-4 py-2 text-left cursor-pointer"
              onClick={() => handleSort("lastActivity")}
            >
              Last Activity
            </th>
            <th className="border px-4 py-2 text-left">Actions</th>
          </tr>
        </thead>
        <tbody>
          {currentNurses.map((nurse) => (
            <tr key={nurse.id} className="hover:bg-gray-100">
              <td className="border px-4 py-2">{nurse.name}</td>
              <td className="border px-4 py-2">
                {nurse.email}
                <br />
                {nurse.phone}
              </td>
              <td className="border px-4 py-2">{nurse.clientsAssigned}</td>
              <td className="border px-4 py-2">
                <span
                  className={`px-2 py-1 rounded ${
                    nurse.status === "Active"
                      ? "bg-green-100 text-green-600"
                      : "bg-gray-100 text-gray-600"
                  }`}
                >
                  {nurse.status}
                </span>
              </td>
              <td className="border px-4 py-2">{nurse.lastActivity}</td>
              <td className="border px-4 py-2">
                <button
                  onClick={() => alert(`Viewing profile for ${nurse.name}`)}
                  className="text-blue-600 hover:underline"
                >
                  View
                </button>
                <button
                  onClick={() => alert(`Editing profile for ${nurse.name}`)}
                  className="text-green-600 hover:underline ml-4"
                >
                  Edit
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Bulk Actions */}
      <div className="flex justify-between items-center mt-6">
        <button
          onClick={handleAssignClient}
          className="bg-gray-600 text-white px-4 py-2 rounded-lg"
        >
          Assign Client to Nurse
        </button>
        <button
          onClick={handleExport}
          className="bg-gray-600 text-white px-4 py-2 rounded-lg"
        >
          Export to CSV
        </button>
      </div>

      {/* Pagination */}
      <div className="flex justify-center items-center mt-6 space-x-2">
        {[...Array(Math.ceil(filteredNurses.length / itemsPerPage)).keys()].map(
          (number) => (
            <button
              key={number}
              onClick={() => handlePageChange(number + 1)}
              className={`px-4 py-2 rounded-lg ${
                currentPage === number + 1
                  ? "bg-blue-600 text-white"
                  : "bg-gray-200 text-gray-600"
              }`}
            >
              {number + 1}
            </button>
          )
        )}
      </div>
    </div>
  );
};

export default Nurses;
