import React, { useState } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import "react-big-calendar/lib/css/react-big-calendar.css";
import moment from "moment";
import DashboardHeader from "../../../components/layout/careseeker/DashboardHeader";

const localizer = momentLocalizer(moment);

const CalendarPage = () => {
  const [events, setEvents] = useState([
    {
      title: "Doctor's Appointment",
      start: new Date(2025, 0, 15, 10, 0),
      end: new Date(2025, 0, 15, 11, 0),
      type: "Medical",
    },
    {
      title: "Family Meeting",
      start: new Date(2025, 0, 20, 14, 0),
      end: new Date(2025, 0, 20, 15, 30),
      type: "Social",
    },
  ]);

  const handleSelectSlot = ({ start, end }) => {
    const title = window.prompt("Enter the event title:");
    const type = window.prompt("Enter the event type (e.g., Medical, Social, Reminder):");
    if (title && type) {
      setEvents([...events, { title, start, end, type }]);
    }
  };

  const handleSelectEvent = (event) => {
    if (window.confirm(`Do you want to delete the event: '${event.title}'?`)) {
      setEvents(events.filter((e) => e !== event));
    }
  };

  return (
    <div>
      <DashboardHeader />
      <div className="max-w-7xl mx-auto px-4 pt-16 pb-8">
        <h1 className="text-2xl font-bold text-gray-800 mb-6">Your Calendar</h1>
        <p className="text-gray-600 mb-4">
          Manage appointments, reminders, and your schedule with ease.
        </p>

        {/* Quick Actions Section */}
        <div className="bg-white p-6 rounded-lg shadow-md mb-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-4">Quick Actions</h2>
          <ul className="list-disc list-inside text-gray-600">
            <li>Add medical appointments to keep track of doctor visits.</li>
            <li>Set reminders for medications and wellness check-ins.</li>
            <li>Schedule family or friend visits conveniently.</li>
            <li>Plan your upcoming week at a glance.</li>
          </ul>
        </div>

        {/* Calendar Component */}
        <div className="bg-white p-6 rounded-lg shadow-md">
          <Calendar
            localizer={localizer}
            events={events.map((event) => ({
              ...event,
              title: `${event.type}: ${event.title}`,
            }))}
            startAccessor="start"
            endAccessor="end"
            selectable
            style={{ height: 500 }}
            onSelectSlot={handleSelectSlot}
            onSelectEvent={handleSelectEvent}
          />
        </div>

        {/* Notifications Section */}
        <div className="bg-white p-6 rounded-lg shadow-md mt-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-4">Notifications</h2>
          <p className="text-gray-600">
            Enable notifications to stay updated on upcoming appointments and reminders.
          </p>
          <button className="mt-4 bg-emerald-500 text-white px-4 py-2 rounded-lg hover:bg-emerald-600">
            Enable Notifications
          </button>
        </div>
      </div>
    </div>
  );
};

export default CalendarPage;
